echo json_encode(Database::execSelect("SELECT Reports.*, Ranking.name, Beatmaps.BeatmapId, Beatmaps.MapsetId, Comments.MedalId, Comments.VersionId, Comments.ProfileId, Comments.Username, Comments.UserId, Comments.PostDate, Comments.ParentComment, Comments.ParentCommenter FROM Reports LEFT JOIN Ranking as Ranking on Ranking.id = Reports.ReporterId LEFT JOIN Beatmaps as Beatmaps ON Reports.Type = 0 AND Reports.ReferenceId = Beatmaps.Id LEFT JOIN Comments as Comments ON Reports.Type = 1 AND Reports.ReferenceId = Comments.Id WHERE Reports.Id = ?;", "i", [$_REQUEST['nReportId']]));